import React from 'react';
import '../index.css';
import { Route,Redirect} from 'react-router-dom';

class Banner extends React.Component{
  constructor(props){
    super(props);
    this.state={
      redirect:false
    };
    this.redirectPage = this.redirectPage.bind(this);
  }
  redirectPage(){
    this.setState({redirect:true})
  }
  render(){
    return(
      <nav id="site-navigation" className="main-navigation" role="navigation">
        <div className="menu-main-container">
          <ul id="primary-menu" className="main-list">
            <li className="menu-item">
              <button onClick={this.props.func.bind(this,"home")}style={{border: "none"}}>Home</button>
            </li>
            <li className="menu-item">
              <button onClick={this.props.func.bind(this,"projects")} style={{border: "none"}}>Projects</button>
            </li>

            <li>
            <Route >
            {this.state.redirect ? <Redirect to="./cv.html" /> : <button onClick={this.state.redirectPage()} style={{border: "none"}}>CV</button>}
            </Route>



            </li>
          </ul>

        </div>
        </nav>
  );
  }

}



export default Banner;
